"""TOML language configuration."""

from typing import Any

from .base import CompositeLanguageConfig, LanguageConfig, language_config_registry
from .plugin_base import LanguagePlugin


class TOMLPlugin(LanguagePlugin):
    """TOML language plugin implementation."""

    @property
    def language_name(self) -> str:
        return "toml"

    @property 
    def supported_node_types(self) -> set[str]:
        return {
            "document",
            "table",
            "pair",
            "array",
            "inline_table",
        }

    def get_chunk_type(self, node_type: str) -> str | None:
        return node_type if node_type in self.supported_node_types else None

    def extract_metadata(self, node: Any, source_code: bytes) -> dict[str, Any]:
        metadata = {"node_type": node.type}

        if node.type == "pair":
            # Extract key name
