    associatedtype ID: Hashable
    var id: ID { get }
}

class Cache<T: Identifiable> {
    private var storage: [T.ID: T] = [:]

    func store(_ item: T) {
        storage[item.id] = item
    }

    func retrieve(id: T.ID) -> T? {
        return storage[id]
    }

    func remove(id: T.ID) {
        storage.removeValue(forKey: id)
    }
}

func findDuplicates<T: Hashable>(_ array: [T]) -> [T] {
    var seen = Set<T>()
    var duplicates = Set<T>()

    for item in array {
        if seen.contains(item) {
            duplicates.insert(item)
        } else {
            seen.insert(item)
        }
    }

    return Array(duplicates)
}

struct Container<T> where T: Equatable {
    private var items: [T] = []

    mutating func append(_ item: T) {
        items.append(item)
    }

    func contains(_ item: T) -> Bool {
        return items.contains(item)
    }
}
'''
        chunks = chunk_text(code, language="swift")
        assert len(chunks) >= 4  # protocol, generic class, function, struct

