#!/usr/bin/env python3
"""Test the REST API endpoints."""

import requests
import json

BASE_URL = "http://localhost:8000"

def test_health():
    """Test health endpoint."""
    print("Testing /health endpoint...")
    response = requests.get(f"{BASE_URL}/health")
    print(f"Status: {response.status_code}")
    print(f"Response: {json.dumps(response.json(), indent=2)}")
    print()

def test_languages():
    """Test languages endpoint."""
    print("Testing /languages endpoint...")
    response = requests.get(f"{BASE_URL}/languages")
    print(f"Status: {response.status_code}")
    data = response.json()
    print(f"Available languages: {', '.join(data['languages'])}")
    print()

def test_chunk_text():
    """Test chunk text endpoint."""
    print("Testing /chunk/text endpoint...")

    test_code = '''def hello_world():
    """Print hello world."""
    print("Hello, World!")

def add_numbers(a, b):
    """Add two numbers."""
    return a + b

class Calculator:
    """Simple calculator class."""

    def multiply(self, x, y):
        """Multiply two numbers."""
        return x * y
'''

    payload = {
        "content": test_code,
        "language": "python",
        "min_chunk_size": 1  # Include small chunks
    }

    response = requests.post(f"{BASE_URL}/chunk/text", json=payload)
    print(f"Status: {response.status_code}")

    if response.status_code == 200:
        data = response.json()
        print(f"Found {data['chunk_count']} chunks:")
        for i, chunk in enumerate(data['chunks'], 1):
            print(f"  {i}. {chunk['node_type']} at lines {chunk['start_line']}-{chunk['end_line']}")
    else:
        print(f"Error: {response.text}")
    print()

def test_chunk_file():
    """Test chunk file endpoint with base64 encoded content."""
    print("Testing /chunk/file endpoint...")

    import base64

    test_code = '''function greet(name) {
    console.log(`Hello, ${name}!`);
}

const add = (a, b) => a + b;

class Person {
    constructor(name) {
        this.name = name;
    }

    sayHello() {
        console.log(`Hi, I'm ${this.name}`);
    }
}
'''

    # Encode content as base64
    encoded_content = base64.b64encode(test_code.encode()).decode()

    payload = {
        "filename": "test.js",
        "content": encoded_content,
        "language": "javascript"
    }

    response = requests.post(f"{BASE_URL}/chunk/file", json=payload)
    print(f"Status: {response.status_code}")

    if response.status_code == 200:
        data = response.json()
        print(f"Found {data['chunk_count']} chunks in {data['filename']}:")
        for i, chunk in enumerate(data['chunks'], 1):
            print(f"  {i}. {chunk['node_type']} at lines {chunk['start_line']}-{chunk['end_line']}")
    else:
        print(f"Error: {response.text}")

if __name__ == "__main__":
    print("Testing Tree-sitter Chunker REST API")
    print("=" * 40)

    try:
        test_health()
        test_languages()
        test_chunk_text()
        test_chunk_file()

        print("\nAll tests completed successfully!")
    except requests.exceptions.ConnectionError:
        print("Error: Could not connect to API server at http://localhost:8000")
        print("Make sure the server is running with: python -m uvicorn api.server:app")
    except Exception as e:
        print(f"Error: {e}")

